game.customCSS:
@import url('https://fonts.googleapis.com/css?family=Baloo+2&display=swap');
* {
  scrollbar-width: thin;
  scrollbar-color: #dfd451 white;
}

*::-webkit-scrollbar {
  width: 20px;
}
*::-webkit-scrollbar-track {
  background: white;
}
*::-webkit-scrollbar-thumb {
  background-color: #dfd451;
  border-radius: 20px;
  border: 6px solid white;
}

body, html {
    font-family: 'Baloo 2', cursive;
    background-color: #dfd451;
    background-image:linear-gradient(90deg, rgba(255,255,255,.3), rgba(255,255,255,.7)),repeating-linear-gradient(135deg, transparent, transparent 35px, rgba(255,255,255,.5) 10px, rgba(255,255,255,.5) 65px);
    width: 100vw;
    height: 100vh;
}

.yathScreen {
   border: 0;
   border-radius: 0;
   top: 0;
   bottom: 0;
   padding: 1% 5%;
   box-shadow: 0px 0px 10px 0px rgba(0, 0, 0, 0.3);
   background-image: url(https://res.cloudinary.com/yath/image/upload/v1586028984/une-aventure-dont-vous-etes-le-plombier/background.png);
   background-position: center;
   background-attachment: local;
}

.yathScreen nav button {
   border-radius: 5px;
   color: #000;
   border-color: #000;
   transition: all 0.3s ease 0s;
   font-family: inherit;
   outline: none;
}

.yathScreen nav button:hover {
    color: #000;
    border-color: #dfd451;
    background-color: #dfd451;
}

.yathScreen nav button:active {
   background-color: #f1ecab;
}

.yathScreen nav button:focus {
   background-color: #f1ecab;
}

.yathImage {
  image-rendering: -moz-crisp-edges;
  image-rendering: -webkit-crisp-edges;
  image-rendering: pixelated;
  image-rendering: crisp-edges;
  width: 480px;
}
